Python + Flask import hmac
from flask import Flask, request app = Flask(__name__) @app.route('/', methods=['POST'])
def receive(): sig = hmac.new(b'<your-key>', request.get_data(), 'sha1').hexdigest() received_sig = request.headers['X-Signature'][len('sha1='):] if sig == received_sig: # 请求确实来自于 OneBot pass else: # 假的上报 pass Node.js + Koa const crypto = require('crypto');
const secret = 'some-secret'; // 在 Koa 的请求 context 中
ctx.assert(ctx.request.headers['x-signature'] !== undefined, 401);
const hmac = crypto.createHmac('sha1', secret);
hmac.update(ctx.request.rawBody);
const sig = hmac.digest('hex');
ctx.assert(ctx.request.headers['x-signature'] === `sha1=${sig}`, 403);

